css:
.payslip-container {
    border: 1px solid black;
    padding: 10px;
    font-family: Arial, sans-serif;
}

.print-heading {
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
}

.company-details {
   padding-left: 0;
    width: 100%;
}

.company-details h2 {
    margin: 0;
}

.company-details p {
    margin: 4px 0 0 0;
    font-size: 12px;
}
.payslip-title {
    height: 34px;
    background-color: #f2f2f2;
    display: flex;
    justify-content: center;
    align-items: center;
}

.payslip-title h2 {
    font-size: 15px;
    margin: 0;
    font-weight: bold;
}

.earnings-deductions {
    display: flex;
    justify-content: space-between;
    margin-top: 8px;
}

.table-section {
    width: 48%;
}

.table-section table {
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
}

.table-section th {
    border-bottom: 1px solid black;
    padding: 5px;
    font-weight: bold;
}

.table-section td {
    padding: 5px;
}

.right-align {
    text-align: right;
}

.total-summary {
    display: flex;
    margin-top: 10px;
}

.summary-item {
    width: 50%;
    display: flex;
}

.summary-label {
    font-size: 12px;
    font-weight: bold;
    flex: 1;
}

.summary-value {
    font-size: 12px;
    font-weight: bold;
    text-align: right;
    flex: 1;
}

.net-payable-section {
    margin-top: 10px;
    padding: 8px;
    background-color: #f2f2f2;
    text-align: center;
}

.net-amount {
    font-weight: bold;
    font-size: 16px;
}
