css:
/* ========================================
   PAYSLIP CONTAINER
   ======================================== */
.payslip-container {
    border: 1px solid #000;
    padding: 10px;
    font-family: Arial, sans-serif;
    max-width: 900px;
    margin: 0 auto;
}

/* ========================================
   HEADER SECTION
   ======================================== */
.print-heading {
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    margin-bottom: 10px;
}

.company-details {
    width: 100%;
}

.company-details h2 {
    margin: 0;
    font-size: 18px;
}

.company-details p {
    margin: 4px 0 0 0;
    font-size: 12px;
}

/* ========================================
   PAYSLIP TITLE
   ======================================== */
.payslip-title {
    height: 34px;
    background-color: #f2f2f2;
    display: flex;
    justify-content: center;
    align-items: center;
    border: 1px solid #ccc;
    margin-bottom: 8px;
}

.payslip-title h2 {
    font-size: 15px;
    margin: 0;
    font-weight: bold;
}

/* ========================================
   EMPLOYEE SUMMARY TABLE
   ======================================== */
.employee-summary {
    margin-bottom: 12px;
}

.summary-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 11px;
}

.summary-table .summary-title {
    background-color: #e8e8e8;
    font-weight: bold;
    font-size: 13px;
    padding: 6px 8px;
    text-align: left;
    border: 1px solid #ccc;
}

.summary-table .label-cell {
    font-weight: 600;
    padding: 5px 8px;
    background-color: #f5f5f5;
    border: 1px solid #ddd;
    width: 16%;
    color: #333;
}

.summary-table .value-cell {
    padding: 5px 8px;
    border: 1px solid #ddd;
    width: 17%;
    color: #555;
}

.summary-table .section-divider td {
    border-top: 2px solid #bbb;
}

/* ========================================
   EARNINGS AND DEDUCTIONS GRID
   ======================================== */
.earnings-deductions-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
    margin-bottom: 12px;
}

.grid-section {
    border: 1px solid #ddd;
    background-color: #fff;
    display: flex;
    flex-direction: column;
}

.section-header {
    background-color: #f8f8f8;
    font-weight: bold;
    font-size: 13px;
    padding: 8px 10px;
    border-bottom: 2px solid #000;
    text-align: center;
}

.section-content {
    padding: 8px;
    flex: 1;
}

.item-row {
    display: grid;
    grid-template-columns: 1fr auto;
    gap: 10px;
    padding: 6px 4px;
    border-bottom: 1px solid #f0f0f0;
    align-items: center;
}

.item-row:last-child {
    border-bottom: none;
}

.item-label {
    font-size: 12px;
    color: #333;
    word-wrap: break-word;
}

.item-value {
    font-size: 12px;
    font-weight: 600;
    color: #000;
    text-align: right;
    white-space: nowrap;
}

.empty-message {
    text-align: center;
    font-style: italic;
    color: #999;
    padding: 20px 10px;
    font-size: 12px;
}

/* ========================================
   SECTION TOTALS (Bottom of Each Column)
   ======================================== */
.section-total {
    display: grid;
    grid-template-columns: 1fr auto;
    gap: 10px;
    padding: 10px 12px;
    background-color: #e8f4f8;
    border-top: 2px solid #000;
    font-weight: bold;
    font-size: 13px;
}

.section-total .total-label {
    color: #333;
    text-align: left;
}

.section-total .total-value {
    color: #000;
    text-align: right;
    white-space: nowrap;
}

/* ========================================
   NET PAYABLE SECTION
   ======================================== */
.net-payable-section {
    margin-top: 12px;
    padding: 10px;
    background-color: #f9f9f9;
    text-align: center;
    border: 1px solid #ddd;
}

.net-amount {
    font-weight: bold;
    font-size: 16px;
    margin: 0 0 4px 0;
    color: #000;
}

/* ========================================
   RESPONSIVE DESIGN
   ======================================== */
@media print {
    .payslip-container {
        border: none;
        padding: 0;
    }
    
    .earnings-deductions-grid {
        gap: 8px;
    }
}

/* For smaller screens - stack the grid */
@media screen and (max-width: 768px) {
    .earnings-deductions-grid {
        grid-template-columns: 1fr;
    }
}

html:
{% set company_address = frappe.db.get_value(
    "Company",
    doc.company,
    "address"
) if doc.company else "" %}

{# The doc.employee field is the Company Link ID #}
{# Fetch employee name and other details from Company Link #}
{% set company_link_details = frappe.db.get_value(
    "Company Link",
    doc.employee,
    ["employee", "date_of_joining", "designation", "department", "branch", "category", "division"],
    as_dict=1
) if doc.employee else {} %}

{% set doj = company_link_details.date_of_joining if company_link_details else None %}
{% set employee_link = company_link_details.employee if company_link_details else None %}
{% set designation = company_link_details.designation if company_link_details else None %}
{% set department = company_link_details.department if company_link_details else None %}
{% set branch = company_link_details.branch if company_link_details else None %}
{% set category = company_link_details.category if company_link_details else None %}
{% set division = company_link_details.division if company_link_details else None %}

{# Now fetch Employee statutory details from Employee doctype #}
{% set employee_details = frappe.db.get_value(
    "Employee",
    employee_link,
    ["employee_pf_account", "esic_number", "lin_number", "bank_name", "account_number", "ifsc_code", "gender"],
    as_dict=1
) if employee_link else {} %}

{% set present_days = (doc.total_working_days or 0) - (doc.absent_days or 0) %}

{# Fetch Salary Structure Assignment for this employee and date range #}
{% set salary_assignment = frappe.db.sql("""
    SELECT name, from_date, to_date 
    FROM `tabSalary Structure Assignment`
    WHERE employee = %s
    AND from_date <= %s
    AND (to_date IS NULL OR to_date >= %s)
    ORDER BY from_date DESC
    LIMIT 1
""", (doc.employee, doc.end_date, doc.start_date), as_dict=1) %}

{% set assignment_name = salary_assignment[0].name if salary_assignment else None %}

{# Fetch earnings from Salary Structure Assignment #}
{% set assignment_earnings = [] %}
{% if assignment_name %}
    {% set assignment_earnings = frappe.db.sql("""
        SELECT salary_component, amount
        FROM `tabSalary Details`
        WHERE parent = %s
        AND parenttype = 'Salary Structure Assignment'
        AND parentfield = 'earnings'
        AND amount > 0
        ORDER BY idx ASC
    """, (assignment_name,), as_dict=1) %}
{% endif %}

{# Calculate total for assignment earnings #}
{% set assignment_earnings_total = namespace(value=0) %}
{% for ae in assignment_earnings %}
    {% set assignment_earnings_total.value = assignment_earnings_total.value + (ae.amount or 0) %}
{% endfor %}

{# Create a set of salary components from assignment for easy lookup #}
{% set assignment_components = [] %}
{% for ae in assignment_earnings %}
    {% set _ = assignment_components.append(ae.salary_component) %}
{% endfor %}

<div class="payslip-container">

    <!-- Header -->
    <div class="print-heading">
        <div class="company-details">
            <h2><strong>{{ doc.company }}</strong></h2>

            <p>
                {% if company_address %}
                    <strong>Address:</strong> {{ company_address }}
                {% else %}
                    <strong>Head Office Address:</strong>
                    Bajaj Steel Industries Limited, C-108, M.I.D.C. Industrial Area,
                    Hingna Road, Nagpur, Maharashtra - 440016
                {% endif %}
            </p>
        </div>
    </div>

    <!-- Payslip Title -->
    <div class="payslip-title">
        <h2>
            Payslip for the Month of
            {{ frappe.utils.formatdate(doc.start_date, "MMMM yyyy") }}
        </h2>
    </div>

    <!-- Employee Pay Summary -->
    <div class="employee-summary">
        <table class="summary-table">
            <thead>
                <tr>
                    <th colspan="6" class="summary-title">Employee Pay Summary</th>
                </tr>
            </thead>
            <tbody>
                <!-- Row 1: Basic Information -->
                <tr>
                    <td class="label-cell">Employee Name</td>
                    <td class="value-cell">{{ doc.employee_name }}</td>
                    <td class="label-cell">Gender</td>
                    <td class="value-cell">{{ employee_details.gender or "-" }}</td>
                    <td class="label-cell">Date of Joining</td>
                    <td class="value-cell">{{ frappe.utils.formatdate(doj, "dd-MM-yyyy") if doj else "-" }}</td>
                </tr>
                <tr>
                    <td class="label-cell">Designation</td>
                    <td class="value-cell">{{ designation or "-" }}</td>
                    <td class="label-cell">Department</td>
                    <td class="value-cell">{{ department or "-" }}</td>
                    <td class="label-cell">Branch</td>
                    <td class="value-cell">{{ branch or "-" }}</td>
                </tr>
                <tr>
                    <td class="label-cell">Category</td>
                    <td class="value-cell">{{ category or "-" }}</td>
                    <td class="label-cell">Division</td>
                    <td class="value-cell">{{ division or "-" }}</td>
                    <td class="label-cell">Payment Days</td>
                    <td class="value-cell">{{ doc.payment_days or 0 }}</td>
                </tr>
                
                <!-- Row 2: Statutory Details -->
                <tr class="section-divider">
                    <td class="label-cell">PF Account No</td>
                    <td class="value-cell">{{ employee_details.employee_pf_account or "-" }}</td>
                    <td class="label-cell">ESI Number</td>
                    <td class="value-cell">{{ employee_details.esic_number or "-" }}</td>
                    <td class="label-cell">LIN Number</td>
                    <td class="value-cell">{{ employee_details.lin_number or "-" }}</td>
                </tr>
                <tr>
                    <td class="label-cell">Bank Name</td>
                    <td class="value-cell">{{ employee_details.bank_name or "-" }}</td>
                    <td class="label-cell">Account Number</td>
                    <td class="value-cell">{{ employee_details.account_number or "-" }}</td>
                    <td class="label-cell">IFSC Code</td>
                    <td class="value-cell">{{ employee_details.ifsc_code or "-" }}</td>
                </tr>
                
                <!-- Row 3: Attendance -->
                <tr class="section-divider">
                    <td class="label-cell">Working Days</td>
                    <td class="value-cell">{{ doc.total_working_days or 0 }}</td>
                    <td class="label-cell">Present Days</td>
                    <td class="value-cell">{{ present_days }}</td>
                    <td class="label-cell">Absent Days</td>
                    <td class="value-cell">{{ doc.absent_days or 0 }}</td>
                </tr>
            </tbody>
        </table>
    </div>

    <!-- Earnings and Deductions Section - Grid Layout -->
    <div class="earnings-deductions-grid">
        
        <!-- Salary Assignment Earnings -->
        <div class="grid-section">
            <div class="section-header">Earnings</div>
            <div class="section-content">
                {% if assignment_earnings %}
                    {% for ae in assignment_earnings %}
                    <div class="item-row">
                        <div class="item-label">{{ ae.salary_component }}</div>
                        <div class="item-value">{{ frappe.utils.fmt_money(ae.amount, currency=doc.currency) }}</div>
                    </div>
                    {% endfor %}
                {% else %}
                    <div class="empty-message">No assignment earnings</div>
                {% endif %}
            </div>
            <div class="section-total">
                <div class="total-label">Total</div>
                <div class="total-value">{{ frappe.utils.fmt_money(assignment_earnings_total.value, currency=doc.currency) }}</div>
            </div>
        </div>

        <!-- Computed Earnings -->
        <div class="grid-section">
            <div class="section-header">Computed Earnings</div>
            <div class="section-content">
                {% set computed_items = [] %}
                {% set computed_earnings_total = namespace(value=0) %}
                {% for e in doc.earnings %}
                    {% if e.amount and e.amount > 0 %}
                        {% set _ = computed_items.append(e) %}
                        {% set computed_earnings_total.value = computed_earnings_total.value + e.amount %}
                    {% endif %}
                {% endfor %}
                
                {% if computed_items %}
                    {% for e in computed_items %}
                    <div class="item-row">
                        <div class="item-label">{{ e.salary_component }}</div>
                        <div class="item-value">{{ frappe.utils.fmt_money(e.amount, currency=doc.currency) }}</div>
                    </div>
                    {% endfor %}
                {% else %}
                    <div class="empty-message">No computed earnings</div>
                {% endif %}
            </div>
            <div class="section-total">
                <div class="total-label">Total</div>
                <div class="total-value">{{ frappe.utils.fmt_money(computed_earnings_total.value, currency=doc.currency) }}</div>
            </div>
        </div>

        <!-- Deductions -->
        <div class="grid-section">
            <div class="section-header">Deductions</div>
            <div class="section-content">
                {% set deduction_items = [] %}
                {% set deductions_total = namespace(value=0) %}
                {% for d in doc.deductions %}
                    {# Get salary component details to check employer_contribution and deduct_from_cash_in_hand_only #}
                    {% set component_details = frappe.db.get_value(
                        "Salary Component",
                        d.salary_component,
                        ["employer_contribution", "deduct_from_cash_in_hand_only"],
                        as_dict=1
                    ) %}
                    {% if d.amount and d.amount > 0 and component_details and not component_details.employer_contribution and not component_details.deduct_from_cash_in_hand_only %}
                        {% set _ = deduction_items.append(d) %}
                        {% set deductions_total.value = deductions_total.value + d.amount %}
                    {% endif %}
                {% endfor %}
                
                {% if deduction_items %}
                    {% for d in deduction_items %}
                    <div class="item-row">
                        <div class="item-label">{{ d.salary_component }}</div>
                        <div class="item-value">{{ frappe.utils.fmt_money(d.amount, currency=doc.currency) }}</div>
                    </div>
                    {% endfor %}
                {% else %}
                    <div class="empty-message">No deductions</div>
                {% endif %}
            </div>
            <div class="section-total">
                <div class="total-label">Total</div>
                <div class="total-value">{{ frappe.utils.fmt_money(deductions_total.value, currency=doc.currency) }}</div>
            </div>
        </div>

    </div>

    <!-- Net Pay in Words -->
    <div class="net-payable-section">
        <p class="net-amount">
            Total Net Payable:
            {{ frappe.utils.fmt_money(frappe.utils.flt(doc.net_salary, 2), currency=doc.currency) }}
        </p>
        <p style="font-size: 12px;">
            ({{ frappe.utils.money_in_words(frappe.utils.flt(doc.net_salary, 2), doc.currency) }})
        </p>
    </div>

</div>
