css:
.payslip-container {
    border: 1px solid black;
    padding: 10px;
    margin: 0;
    font-family: Arial, sans-serif;
}

.print-heading {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.company-details {
    padding-left: 4px;
    text-align: left;
}

.company-logo img {
    width: 150px;
}

.payslip-title {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 34px;
    background-color: #f2f2f2;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
}

.payslip-title h2 {
    font-size: 15px;
    margin: 0 !important;
    padding: 0 !important;
    font-weight: bold;
    white-space: nowrap;
}

.pay-summary {
    padding: 10px;
    margin: 0;
}

.summary-header {
    text-align: left;
    font-weight: bold;
    font-size: 14px;
}

.summary-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 5px;
    font-size: 12px;
}

.summary-table td {
    padding: 2px 5px;
}

.earnings-deductions {
    display: flex;
    justify-content: space-between;
    width: 100%;
}

.table-section {
    width: 48%;
}

.table-section table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 5px;
    font-size: 12px;
}

.table-section th {
    text-align: left;
    padding: 5px;
    border-bottom: 1px solid black;
}

.table-section th.right-align {
    text-align: right;
    padding-right: 5px;
}

.table-section td {
    padding: 5px;
}

.right-align {
    text-align: right;
}

.center-align {
    text-align: center;
}

.total-summary {
    display: flex;
    justify-content: space-between;
    margin-top: 10px;
}

.earnings-deductions th {
    color: #000 !important;
}

.total-summary {
    display: flex;
    justify-content: flex-start;
    width: 100%;
}

.summary-item {
    width: 50%;
    display: flex;
}

.summary-label {
    font-size: 12px;
    padding-left: 10px;
    font-weight: bold;
    color: #000;
    flex: 1;
}

.summary-value {
    font-size: 12px;
    font-weight: bold;
    color: #000;
    text-align: right;
    flex: 1;
}

.table-section th {
    font-size: 12px;
    font-weight: bold;
}

.side-by-side-section {
    padding-top: 10px;
    display: flex;
    justify-content: space-between;
    width: 100%;
}

.half-section {
    width: 48%;
}

.reimbursement-section {
    width: 100%;
}

.reimbursement-section h2 {
    font-weight: bold;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 26px;
    background-color: #f2f2f2;
    display: flex;
    font-size: 12px;
    justify-content: left;
    align-items: center;
    text-align: center;
    padding-left: 5px;
}

.reimbursement-table {
    width: 100%;
    border-collapse: collapse;
}

.reimbursement-table td {
    padding: 8px;
    text-align: left;
    font-size: 14px;
}

.reimbursement-table .amount {
    text-align: right;
}

.leave-summary-section {
    width: 100%;
}

.leave-summary-section h2 {
    font-weight: bold;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 26px;
    background-color: #f2f2f2;
    display: flex;
    font-size: 12px;
    justify-content: left;
    align-items: center;
    text-align: center;
    padding-left: 5px;
}

.leave-summary-table {
    width: 100%;
    height: 10px;
    border-collapse: collapse;
}

.leave-summary-table thead {
    background-color: #f2f2f2;
    color: black;
}

.leave-summary-table th, .leave-summary-table td {
    padding: 8px;
    text-align: left;
    font-size: 12px;
}

.additional-salary-section {
    margin-top: 0px !important;
    width: 100%;
}

.additional-salary-heading {
    font-weight: bold;
    margin: 0;
    padding: 5px;
    font-size: 12px;
    background-color: #f2f2f2;
    text-align: left;
    color: #000;
}

.additional-salary-section table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 5px;
    font-size: 12px;
}

.additional-salary-section th, .additional-salary-section td {
    padding: 5px;
    text-align: left;
    border-bottom: 1px solid #ddd;
}

.additional-salary-section th {
    font-weight: bold;
    color: black;
}

.additional-salary-section .right-align {
    text-align: right;
}

.additional-salary-section .center-align {
    text-align: center;
    font-style: italic;
    color: #555;
}

.net-payable-section {
    width: 100%;
    text-align: center;
    font-size: 16px;
    margin-top: 10px;
    padding: 8px;
    background-color: #f2f2f2;
}

.net-amount {
    font-weight: bold;
}

.print-format td, .print-format th {
    padding: 8px !important; 
}
