css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    margin: 0 !important;
    padding: 0 !important;
}

.print-format-gutter,
.print-format.print-format-preview,
.page-container,
.page-content {
    padding: 0 !important;
    margin: 0 !important;
}

.print-format {
    font-family: Arial, Helvetica, sans-serif;
    color: #000;
    width: 100% !important;
    margin: 0 !important;
    padding: 15mm !important;
    background: #fff;
    line-height: 1.3;
}

.header-section {
    text-align: center;
    margin-bottom: 20px;
}

.company-name {
    font-size: 22px;
    font-weight: 700;
    letter-spacing: 1.2px;
    margin-bottom: 10px;
}

.document-title {
    font-size: 16px;
    font-weight: 600;
    color: #333;
}

.period {
    font-size: 13px;
    color: #666;
    margin-top: 5px;
}

.table-section {
    margin-bottom: 40px;
}

.variable-pay-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 13px;
    border: 1px solid #000; /* Outer table border */
}

.variable-pay-table th,
.variable-pay-table td {
    border: 1px solid #000;  /* Borders for all rows and columns */
    padding: 12px 15px;
}

.variable-pay-table thead th {
    background: #f5f5f5;
    font-weight: 700;
    font-size: 12px;
}

.variable-pay-table tfoot td {
    background: #f5f5f5;
    font-weight: 700;
}

.bold {
    font-weight: 700;
}

.text-left {
    text-align: left;
}

.text-center {
    text-align: center;
}

.text-right {
    text-align: right;
}

.signature-section {
    display: flex;
    justify-content: center;
    margin-top: 50px;
    margin-bottom: 25px;
}

.signature-box {
    width: 35%;
    text-align: center;
}

.signature-line {
    border-top: 1.5px solid #000;
    margin-bottom: 8px;
}

.signature-label {
    font-size: 11px;
    color: #666;
}

.print-footer {
    text-align: center;
    margin-top: 30px;
    padding-top: 12px;
    border-top: 1px solid #ccc;
}

.print-footer p {
    font-size: 10px;
    color: #999;
}

@media print {
    @page {
        size: A4;
        margin: 8mm;
    }

    .print-format {
        padding: 8mm !important;
    }

    .variable-pay-table thead th,
    .variable-pay-table tfoot td {
        background: #f5f5f5 !important;
        -webkit-print-color-adjust: exact;
        print-color-adjust: exact;
    }
}

@media screen and (max-width: 768px) {
    .print-format {
        padding: 10mm !important;
    }

    .company-name {
        font-size: 18px;
    }

    .document-title {
        font-size: 14px;
    }

    .signature-box {
        width: 70%;
    }
}
